import React from "react";
import InnerList from "./innerList";
import './item.css'
export default class MyItem extends React.Component {
    constructor(props) {
        super(props)
        this.state = {done: false, inner: false, tag: props.tag}
        this.doneMaker = this.doneMaker.bind(this)
    }

    doneMaker() {
        this.setState({done: !this.state.done})
        this.setState({inner: !this.state.inner})
    }
    render() {
        return(
            <li className={this.state.done ? 'todo-item done' : 'todo-item active'} onClick={this.doneMaker} key={new Date}>{this.props.item.text}
                 <form className="inner-form" action="">
                    <input className="inner-input" type="text"/>
                    <button className="inner-button">Добавить подзадачу</button>
                    </form>
            </li>
        )
    }
}
