import React from "react";
import './innerlist.css'
export default class InnerList extends React.Component {
    constructor(props) {
        super(props)
        this.state = {items: [
            {text: 'помыть полы',
            done: false
        },
        {text: 'поесть',
            done: false
        },
        {text: 'полить цветы',
            done: false
        }
        ],opened: props.innerOpened}
    }

    render() {
        return( 
            <ul className={this.state.opened ? 'open' : 'closed'}>
                {this.state.items.map(item=>

                    <li>{item.text}</li>
                    )}
            </ul>
        )
    }
}
