import React from "react";
import './item.css'
export default class MyItem extends React.Component {
    constructor(props) {
        super(props)
        this.state = {done: false}
        this.doneMaker = this.doneMaker.bind(this)
    }

    doneMaker() {
        this.setState({done: !this.state.done})
        let classname = this.state.done ? 'done' : 'active'
        return classname
    }
    render() {
        return(
            <li className={this.doneMaker} onClick={this.doneMaker} key={new Date}>{this.props.item.text}</li>
        )
    }
}
