import React from "react"
import InnerList from "./innerList"
import MyInput from "./input"
import MyItem from "./item"
import './list.css'

export default class MyList extends React.Component {
    constructor(props) {
        super(props)
        this.state = {opened: false, inner: false}
        this.checker = this.checker.bind(this)
        this.opener = this.opener.bind(this)


    }
    checker(state) {
        let classname = state ? 'open' : 'closed'
        return classname
    }
    opener() {
        this.setState({opened: !this.state.opened})
        console.log(this.state.opened)
    }

    render() {
        return(
            <div>
                <h1 onClick={this.opener}>TODO</h1>
                 <ul className={this.checker(this.state.opened)}>
                    {this.props.items.map(item=> 
                        <MyItem item={item}>
                            <form className="inner-form" action="">
                    <input className="inner-input" type="text"/>
                    <button className="inner-button">Добавить подзадачу</button>
                    </form>
                        </MyItem>
                        )
                    }


                </ul>
            </div>
        )
    }
}
