import React from "react";

export default class MyInput extends React.Component {
    constructor(props) {
        super(props)
        this.state = {items: [], text: ''}
        this.handler = this.handler.bind(this)
        this.submitter = this.handler.bind(this)
    }
    handler(e) {
        this.setState.text = e.target.value
    }
    submitter(e) {
        e.preventDefault()
        if (this.state.text.length === 0) {
            return;
        }
        this.setState.state = [...this.state.items, {text: this.state.text}]
    }
    render() {
        return (

        <div>
            <input onChange={this.handler} value={this.state.text} type="text" />
            <button onClick={this.submitter}></button>
        </div>
        )
    }
}

class MyList extends React.Component {

    render() {
        return(
            <ul>
                {this.props.items.map(item=> 
                    (<li id={new Date}>{item.text}</li>))}
            </ul>
        )
    }
}
