import React from "react";
import InnerList from "./innerList";
import './item.css'
export default class MyItem extends React.Component {
    constructor(props) {
        super(props)
        this.state = {done: false, inner: false}
        this.doneMaker = this.doneMaker.bind(this)
    }

    doneMaker() {
        this.setState({done: !this.state.done})
        this.setState({inner: !this.state.inner})
    }
    render() {
        return(
            <li className={this.state.done ? 'done' : 'active'} onClick={this.doneMaker} key={new Date}>{this.props.item.text}

            <InnerList inner={this.state.inner}></InnerList>
            </li>
        )
    }
}
