import React from "react";
import MyList from "./list";

export default class MyInput extends React.Component {
    constructor(props) {
        super(props)
        this.state = {items: [], text: '', tags: []}
        this.handler = this.handler.bind(this)
        this.submitter = this.submitter.bind(this)
    }
    render() {
        return (
            <div>
                <form onSubmit={this.submitter}>
                    <input type="text" onChange={this.handler} value={this.state.text}/>
                    <button>Нажми на меня</button>
                    <select value='main' name="choice" id="select">
                        <option>main</option>
                        {this.state.tags.map(tag=> <option>{tag.tag}</option>)}
                    </select>
                </form>
                <MyList tags={this.state.tags} items={this.state.items}></MyList>
            </div>

        )
    }
    handler(e) {
        this.setState({ text: e.target.value });
    }
    submitter(e) {
        e.preventDefault();
        if (this.state.text.length === 0) {
            return;
        }
        this.setState({items: [...this.state.items, {text: this.state.text}], tags: [...this.state.tags, {tag: this.state.text}]})
    }
}
